var map_endpoint_to_response = flow.get("map_endpoint_to_response");
var rest_put_format = flow.get("rest_put_format");

// parses the requested operating mode to the corresponding integer
// setpoint is either an integer or the corresponding mode-string
var parseOperatingMode = function(setpoint) {
    // possible states and its id
    const mode_strings = {
        "AUTOMATIC_CYCLE": 0,
        "MEASURE_TANK_A": 1,
        "MEASURE_TANK_B": 2,
        "DRAIN_SENSOR": 3,
        "STOP": 4
    };

    let id = mode_strings[setpoint];
    if (isNaN(id)) {
        id = parseInt(setpoint)
        if (id < 0 || 4 < id || isNaN(id)) {
            id = null;
            msg.statusCode = 400;
            msg.payload = "Unknown Status: " + setpoint + ".";
        }
    }
    return id;
}

// recursively copies the requested values from the json request into a setpoint object
//   format: mapping of a subset of the full json-rest-structure to its key in a setpoint object
//   payload: same subset of the json-rest-structure as "format" but with the requested values
//   setpoint: setpoint object to fill
var addToSetpoint = function(format, payload, setpoint){
    //check if input is null (if format and payload do not match)
    if(format === null || payload === null){
        msg.statusCode = 400;
        msg.payload = "Body of request does not fit the expected format";
        setpoint = {};
    }
    // if current subset of the json-rest-structure has more parameters
    if (typeof format === 'object') {
        // recursively fill each parameter
        for (let key in format) {
            addToSetpoint(format[key], payload[key], setpoint);
        }
    }
    // if current subset is a single parameter fill in the value
    else {
        //special handling of auto-tide
        if(format === "auto_tide_a" || 
                format === "auto_tide_b"){
            let modes = global.get("modes");
            modes[format] = payload;
        }
        //special handling of operating_mode
        else if (format === "operating_mode") {
            let modes = global.get("modes");
            modes[format] = parseOperatingMode(payload);
        }
        else if(format !== -1){
            setpoint[format] = payload;
        }
    }
}


/* return the requested setpoint with given url and payload */
var constructSetpoint = function (url, payload) {
    // remove trailing slashes
    url = url.endsWith('/') ? url.slice(0, -1) : url;

    // get the path to the nested parameter
    var path = map_endpoint_to_response[url].split(".");
    // get mapping from rest-response-object to setpoint-keys
    var format = RED.util.cloneMessage(rest_put_format);

    // get only the subset of the mapping that is requested
    for (let i in path) {
        format = format[path[i]];
    }
    // so that payload and format have the same structure
    payload = payload[path.at(-1)];

    // fill the setpoint and return it
    var setpoint = {};
    addToSetpoint(format, payload, setpoint);
    return setpoint;
}

