var getOperatingModeString = function(mode_number) {
    switch (mode_number) {
        case 0:
            return "AUTOMATIC_CYCLE";
        case 1:
            return "MEASURE_TANK_A";
        case 2:
            return "MEASURE_TANK_B";
        case 3:
            return "DRAIN_SENSOR";
        case 4:
            return "STOP";
    }
}

/* returns the corresponding name for a given step number */
var getStatusString = function (step_number) {
    switch (step_number) {
        case 0:
            return "STOP";
        case 1:
            return "PREP_TANK_A";
        case 2:
            return "VENT_TANK_A";
        case 3:
            return "RUN_TANK_A";
        case 4:
            return "STOP_TANK_A";
        case 5:
            return "DRAIN_TANK_A";
        case 6:
            return "PREP_TANK_B";
        case 7:
            return "VENT_TANK_B";
        case 8:
            return "RUN_TANK_B";
        case 9:
            return "STOP_TANK_B";
        case 10:
            return "DRAIN_TANK_B";
    }
}

/* recursively fill the response object from current sps_status
    using the given mapping*/
var fillResponseFromSpsStatus = function(response, map) {
    for (let key in response){
        // if current parameter is object fill its nested parameters
        if (typeof response[key] === 'object' && response[key] !== null) {
            fillResponseFromSpsStatus(response[key], map[key]);
        }
        // if current parameter is a simple value assign it and break recursion
        else{
            // if the value should not be copied from sps-status, but instead from the current setpoint etc.
            // it maps to -1
            if(map[key] !== -1){
                // special handling of auto-tide and operating mode
                if (key === "auto_tide" || key === "operating_mode") {
                    let modes = global.get("modes");
                    response[key] = modes[map[key]];
                    if (key === "operating_mode") {
                        response[key] = getOperatingModeString(response[key]);
                    }
                }
                else{
                    response[key] = sps_status[map[key]];

                    // replace step number with status string
                    if(key === "state"){
                        response[key] = getStatusString(response[key]);
                    }
                }
            }
        }
    }
    //node.warn(response);
    return response;
}

/* return the nested object for a request with given url */
var extractSpecificResponse = function(url) {
    var response = RED.util.cloneMessage(rest_response);

    // remove trailing slashes
    url = url.endsWith('/') ? url.slice(0, -1) : url;

    // get the path to the nested parameter
    var path = map_endpoint_to_response[url].split(".");
    // select the requested nested parameter
    //node.warn(path);
    for(let index in path){
        response = response[path[index]];
        if(response === null){
            msg.statusCode = 500;
        }
    }

    // return the response as the key-value pair from the full response-json
    return {[path.at(-1)]: response};
}

fillResponseFromSpsStatus(rest_response, map_response_to_status);

//unless something goes wrong set response status to OK
msg.statusCode = 200
msg.payload = extractSpecificResponse(msg.req.url);

return msg;

