            rest_response["system"]["tank_a"]["temperature"]["target"] = parseFloat((0.01 * msg.setpoint[x]).toFixed(2));
            break;
        case "TEMP_SETPOINT_B":
            rest_response["system"]["tank_b"]["temperature"]["target"] = parseFloat((0.01 * msg.setpoint[x]).toFixed(2));
            break;
    }
}
